import firebase from "./firestore";

const addMeasurement = e => {
  e.preventDefault();
  const db = firebase.firestore();
  db.settings({
    timestampsInSnapshots: true
  });
  const measurementsRef = db.collection("measurements").add({
    date: "test",
    value: 5
  });
};

export default addMeasurement;
